1311006221371_1_28.text:
Old Example
#@@@
{ "Result" : // wrapper
{
  "postTime"       : "1985-04-13T17:10:00.57Z", // must
  "solutionGetTime": "1985-04-13T17:10:00.55Z", // must
  "ID"             : "#1", // must, generated by CC
  "comment"        : "computation time: 0.023s", // opt

  "Solution": // exact copy of Solution this Result has been computed for
  { "postTime" : "1985-04-13T17:10:00.52Z", // generated by SC
    "ID"       : "#37" // generated by SC
    //...
  }, // Solution

  "computation": // info about computation
  {
    "userInfo":
    {
      "CC_versionLong": "[ViP CCv1.16 (C chain)v1.1.4 (Matlab chain)v1.7 (DuMuX chain)v0.4 (Thu Jul 14 21:02:41 CEST 2011)]",
      "CC_version"    : "ViP CCv1.16",
      "chainVersion"  : "(Matlab chain)v1.7",
      // "chainType"     : "Matlab", -> see Exercise
      // "status"        : "failure", // or "success" -> see "error"
      "error"         : // null, if there is no error
      { "phase"       : "interpreting",
        "typeGeneric" : "internalExpected", // | "internalUnexpected" | "external" (for err exits of external commands)
        //"returnCode"  : 66, // only there, if "typeGeneric" == "external" (not a case for Matlab)
        "typeSpecific": "output", // | "input" | "timeout" // if "typeGeneric" is "internalExpected"
                         // | "system" // if "typeGeneric" is "internalUnexpected"
                         // | "start" // may occur in both "internalExpected" (some executable parametrized by teacher), "internalUnexpected" (gcc)
        "position"    : { "row": 47, "col": 11 }, // opt: if there is one available/[filtering implemented]
        "message"     : "';' missing at line: 47 column: 11." // opt: may be filtered from output
      }
    },
    "technicalInfo": // null or object; to be flexible val has no fixed format: ..
    // .. for bug reports it should just be output'ed as JSON string (in case of statusCode > 0).
    { "host"   : "nflvip1", // CC host
      "PID"    : 4711,      // CC PID
      // "time"   : "1985-04-13T17:10:00.55Z", -> see times at beginning of Result
      "exception": "VeryBadException", // opt: if there is one
      "message": "whatever may be of interest",
      "someUnknownIdent": #someUnknownValue#
    }
  }, // "computation" val

  "elements": // opt/must: opt, if err (status->code > 0); must otherwise (no err)
  [
    { "identifier": "output_1",                       // must: technical
      "name"      : "Output of Executable",           // opt: name of element (may be shown in SC)
      "MIMEtype"  : "text/plain",                     // opt; default: "text/plain"
      "value"     : "Primes(20): 2 3 5 7 11 13 17 19" // must: content
    }
  ]
}
} // "Result"
#@@@

